mapper.update:
update sk_blog
author=#{author}, title=#{title}, content=#{content}, image_url=#{imageUrl}, blog_nature=#{blogNature}, label_name=#{labelName}, type_name=#{typeName}, label_id=#{labelId}, type_id=#{typeId}, reading_quantity=#{readingQuantity}, like_num_int=#{likeNumInt}, is_open_home_recommend=#{isOpenHomeRecommend}, is_open_copyright=#{isOpenCopyright}, is_open_comment=#{isOpenComment}, status=#{status}, publish_year=#{publishYear}, publish_month=#{publishMonth}, update_user=#{updateUser}, update_time=#{updateTime}
where blog_id=#{blogId}

mapper.select:
select * from sk_blog

    and label_id=#{term.labelId}
    and type_id=#{term.typeId}
    and reading_quantity=#{term.readingQuantity}
    and like_num_int=#{term.likeNumInt}
    and is_open_home_recommend=#{term.isOpenHomeRecommend}
    and is_open_copyright=#{term.isOpenCopyright}
    and is_open_comment=#{term.isOpenComment}
    and status=#{term.status}

order by creation_time desc

select * from sk_blog

    and label_id=#{term.labelId}
    and type_id=#{term.typeId}
    and reading_quantity=#{term.readingQuantity}
    and like_num_int=#{term.likeNumInt}
    and is_open_home_recommend=#{term.isOpenHomeRecommend}
    and is_open_copyright=#{term.isOpenCopyright}
    and is_open_comment=#{term.isOpenComment}
    and status=#{term.status}

order by creation_time desc limit ${topCurrPage},${pageSize};

select count(*) from sk_blog

    and label_id=#{term.labelId}
    and type_id=#{term.typeId}
    and reading_quantity=#{term.readingQuantity}
    and like_num_int=#{term.likeNumInt}
    and is_open_home_recommend=#{term.isOpenHomeRecommend}
    and is_open_copyright=#{term.isOpenCopyright}
    and is_open_comment=#{term.isOpenComment}
    and status=#{term.status}
